import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, ImageBackground, Image, TouchableOpacity, FlatList, SafeAreaView } from 'react-native';
import { Actions } from 'react-native-router-flux';
import bgimage from '../assets/backgrounds/correctBg.jpg'
import correct from '../img/correrct.png'

export default function CorrectQuestionPage() {

  return (
    <ImageBackground source={bgimage} style={styles.background}>
      <View style={styles.BoxQ}>
        <View style={styles.topicC}>
          <Text style={styles.TopicQ}>
            Correct
          </Text>
        </View>
        <View style={styles.im}>
          <Image source={correct} style={styles.imagecorrect} />
        </View>
        <View style={styles.con}>
          <TouchableOpacity style={styles.btnlogin} onPress={() => Actions.storyPage()}>
            <Text style={styles.textstyle}>Continue</Text>
          </TouchableOpacity>
        </View>
        <StatusBar style="auto" />
      </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  }, BoxQ: {
    width: 300,
    height: 400,
    backgroundColor: 'rgba(0, 0, 0, 0.10)',
    borderRadius: 25,
    alignItems: 'center'
  },
  Topic: {
    margin: 20,
  },
  TopicQ: {
    fontSize: 50,
    fontWeight: 'bold',
    color: '#00FF7F',
    borderBottomWidth: 3,
    margin: 10,
    width: 250,
    textAlign:'center'
  },
  item: {
    backgroundColor: 'rgba(0, 70, 150, 0.29)',
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
    borderRadius: 25,
  },
  title: {
    fontSize: 32,

  },
  flat: {
    margin: 20
  },
  im: {
    marginTop: -45
  },
  imagecorrect: {
    width: 300,
    height: 300,
  },
  btnlogin: {
    width: 250,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#2E8B57',
    justifyContent: 'center',
    marginTop: 20,
    alignItems: 'center',
  },
  con: {
    marginTop: -50
  },
  textstyle: {
    color: '#00FF7F',
    fontWeight: 'bold',
    fontSize: 25
  }
});
